#include <stdint.h>
#include "board/timer.h"
#include "board/rcc.h"
#include "board/gpio.h"
#include "board/iser.h"
#include "utils.h"
#include "motors.h"
#include "joystick.h"
#include "leds.h"
#include "drive.h"
#include "board/adc.h"
#include "board/exti.h"

void init_extis(){
	//Push button
	SET(RCC_AHB2ENR, GPIOEEN);

	SET_BITS(GPIOE->MODER, 15 * 2, INPUT_MODE, 2);
	SET_BITS(GPIOE->PUPDR, 15 * 2, PULLUP, 2);

	EXTI->EXTISR[15] = 4;
	SET(EXTI->IMR1, 15);
	SET(EXTI->FTSR1, 15);
	SET(ISER0, 26);

	//Blue button
	SET(RCC_AHB2ENR, GPIOCEN);

	SET_BITS(GPIOC->MODER, 13 * 2, INPUT_MODE, 2);
	SET_BITS(GPIOC->PUPDR, 13 * 2, PULLDOWN, 2);

	EXTI->EXTISR[13] = 2;
	SET(EXTI->IMR1, 13);
	SET(EXTI->RTSR1, 13);
	SET(ISER0, 24);
}

void ADC1_2_IRQHandler() {

	drive();
	SET(ADC1->ISR, ADC_JEOS);
}


void EXTI15_IRQHandler() {
	joystick_button_handler();
	SET(EXTI->FPR1, 15);
}

void EXTI13_IRQHandler() {
	static int auto_mode = 0;
	enable();
	if(auto_mode){
		auto_mode = 0;
		set_mode(MANUAL);
	}else{
		init_mode(AUTO_WAIT);
		auto_mode = 1;
	}
	SET(EXTI->RPR1, 13);
}

int main(void) {
	init_mode(MANUAL);
	init_motors();
	init_extis();
	init_leds();
	set_led_direction(LED_STOP);
	initialize_adc();
	init_ultrasonic();
	while (1) {
//		set_led_direction(LED_STOP);
	    for(int i=0; i<=3330; i++);
		SET(ADC1->CR, ADC_JADSTART);
	}
}
